@import "tailwindcss";
@import "tw-animate-css";

@custom-variant dark (&:is(.dark *));

@theme inline {
  --radius-sm: calc(var(--radius) - 4px);
  --radius-md: calc(var(--radius) - 2px);
  --radius-lg: var(--radius);
  --radius-xl: calc(var(--radius) + 4px);

  --color-background: hsl(var(--background));
  --color-foreground: hsl(var(--foreground));

  --color-card: hsl(var(--card));
  --color-card-foreground: hsl(var(--card-foreground));

  --color-popover: hsl(var(--popover));
  --color-popover-foreground: hsl(var(--popover-foreground));

  --color-primary: hsl(var(--primary));
  --color-primary-foreground: hsl(var(--primary-foreground));

  --color-secondary: hsl(var(--secondary));
  --color-secondary-foreground: hsl(var(--secondary-foreground));

  --color-muted: hsl(var(--muted));
  --color-muted-foreground: hsl(var(--muted-foreground));

  --color-accent: hsl(var(--accent));
  --color-accent-foreground: hsl(var(--accent-foreground));

  --color-destructive: hsl(var(--destructive));
  --color-destructive-foreground: hsl(var(--destructive-foreground));

  --color-border: hsl(var(--border));
  --color-input: hsl(var(--input));
  --color-ring: hsl(var(--ring));

  --font-sans: 'DM Sans', sans-serif;
  --font-serif: 'Playfair Display', serif;
}

/* PROFESSIONAL MINIMALIST THEME - Pink & White */
:root {
  /* Clean White Background */
  --background: 0 0% 100%; 
  --foreground: 340 10% 20%; /* Warm grey/brown text */

  /* Crisp White Cards */
  --card: 0 0% 100%;
  --card-foreground: 340 10% 20%;

  --popover: 0 0% 100%;
  --popover-foreground: 340 10% 20%;

  /* The Main Color: #f97a9d -> HSL 343 92% 73% */
  --primary: 343 92% 73%; 
  --primary-foreground: 343 10% 10%; /* Darker text on light pink for readability, or white if contrast allows. 73% lightness is quite light, white text might be hard to read. Let's try white first, or dark red. */
  --primary-foreground: 0 0% 100%; /* Trying white, but might need text-shadow or darker pink for button text */

  /* Very subtle pinkish grey for secondary */
  --secondary: 343 20% 96%;
  --secondary-foreground: 343 67% 45%;

  --muted: 343 10% 96%;
  --muted-foreground: 343 10% 60%;

  --accent: 343 30% 96%;
  --accent-foreground: 343 67% 45%;

  --destructive: 0 84% 60%;
  --destructive-foreground: 0 0% 98%;

  --border: 343 20% 90%;
  --input: 343 20% 90%;
  --ring: 343 92% 73%;

  --radius: 0.5rem;
}

.dark {
  /* Dark mode version if needed */
  --background: 343 30% 10%;
  --foreground: 343 10% 95%;
  --card: 343 30% 12%;
  --card-foreground: 343 10% 95%;
  --primary: 343 92% 65%; /* Slightly darker for better contrast in dark mode */
  --primary-foreground: 0 0% 100%;
}

@layer base {
  * {
    @apply border-border;
  }
  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
  h1, h2, h3, h4, h5, h6 {
    @apply font-serif tracking-tight;
  }
}

@layer utilities {
  .container-padding {
    @apply px-6 md:px-12 lg:px-24;
  }
}

